import * as axios from "axios";
export default {
  inject: ['reload'],
  name: "category",
  data: function data() {
    return {
      isEdit: true
    };
  },
  methods: {
    handleAdd: function handleAdd(node) {
      console.log("add .... ");
      console.log(node.name);
      this.$http.post('http://api.leyou.com/api/item/category/add', {
        data: {
          'isParent': node.isParent,
          'name': node.name,
          'parentId': node.parentId,
          'sort': node.sort
        }
      }).then(function (response) {// console.log (response)
      });
    },
    handleEdit: function handleEdit(id, name) {
      console.log("edit... id: " + id + ", name: " + name);
      this.$http.post("/item/category/edit?id=" + id + "&name=" + name);
      this.reload();
    },
    handleDelete: function handleDelete(id) {
      var _this = this;

      console.log("delete ... " + id); // this.$http.post("api.leyou.com/api/item/category/delete?id=" + id);

      this.$http.post('/item/category/of/delete', {
        params: {
          'id': id
        }
      }).then(function (response) {
        _this.$message.info('删除成功');

        _this.reload();
      }).catch(this.$message.info('删除失败'));
    },
    handleClick: function handleClick(node) {
